section.programlisting:
#include <stdio.h>

int
factorial( int n )
{
  int
    ret = 1;

  while( n > 0 )
  {
    ret *= n--;
  }

  return( ret );
}

int
main( void )
{
  int
    t = 0, /* Tesztesetek szama. */
    m = 0; /* Matrix merete.   */

  for( scanf( "%d", &t ) ; t > 0 ; --t )
  {
    scanf( "%d", &m );
    printf( "%d\n", factorial( 2 * m ) / (factorial( m ) * factorial( m )) );
  }

  return( 0 );
}
